interface APIUser {
  id: number
  email: string
  name: string | null
  team_id: number | null
}

interface APITeam {
  id: number
  name: string
  project: {
    name: string
    description: string
    demo_url: string | null
    repo_url: string | null
    submitted: boolean
  }
}

interface GetUserResponse extends APIUser {
  team: APITeam | null
}

type CreateTeamResponse = APITeam

type GetTeamMembersResponse = {
  id: number
  email: string
  name: string | null
  team_id: number | null
}[]

interface UpdateUserResponse {
  message: string
}
